const e="<template>\r\n  <div class=\"container\">\r\n    <div class=\"cesiumContainer\" id=\"cesiumContainer\"></div>\r\n  </div>\r\n</template>\r\n\r\n<script setup>\r\nimport * as Cesium from 'cesium'\r\nimport { onMounted } from 'vue'\r\n\r\nlet viewer\r\n\r\nonMounted(() => {\r\n  viewer = new Cesium.Viewer('cesiumContainer', {\r\n    animation: false, // 是否创建动画小器件，左下角仪表\r\n    baseLayerPicker: false, // 是否显示图层选择器\r\n    fullscreenButton: false, // 是否显示全屏按钮\r\n    vrButton: false, // 用于切换 VR 模式的单个按钮小部件。\r\n    geocoder: false, // //是否显示geocoder小器件，右上角查询按钮\r\n    homeButton: false, // 是否显示Home按钮\r\n    infoBox: false, // 是否显示信息框\r\n    sceneModePicker: false, // 是否显示3D/2D选择器\r\n    selectionIndicator: false, // 是否显示选取指示器组件\r\n    timeline: false, // 是否显示时间轴\r\n    navigationHelpButton: false, // 是否显示右上角的帮助按钮\r\n    navigationInstructionsInitiallyVisible: false,\r\n    scene3DOnly: false, // 如果设置为true，则所有几何图形以3D模式绘制以节约GPU资源\r\n    shouldAnimate: false, // 初始化是否开始动画\r\n    baseLayer: false\r\n  })\r\n  viewer.scene.globe.maximumScreenSpaceError = 1.4 // 减少屏幕空间误差，提高渲染质量\r\n\r\n  const imageryProvider = new Cesium.OpenStreetMapImageryProvider({\r\n    url: '//a.tile.openstreetmap.org/'\r\n  })\r\n  viewer.imageryLayers.addImageryProvider(imageryProvider)\r\n\r\n  const position = Cesium.Cartesian3.fromDegrees(113.297730, 23.060679, 50000)\r\n  viewer.camera.setView({\r\n    destination: position,\r\n    orientation: {\r\n      heading: 0,\r\n      pitch: Cesium.Math.toRadians(-90),\r\n      roll: 0\r\n    }\r\n  })\r\n})\r\n<\/script>\r\n<style scoped>\r\n.container {\r\n  position: relative;\r\n  width: 100%;\r\n  height: 100%;\r\n  .cesiumContainer {\r\n    width: 100%;\r\n    height: 100%;\r\n    overflow: hidden\r\n  }\r\n}\r\n</style>\r\n";export{e as default};
