const e="<template>\r\n  <div class=\"container\">\r\n    <div class=\"btn_wrapper\">\r\n      <el-button type=\"primary\" size=\"small\" @click=\"toggleLayer('img')\">影像</el-button>\r\n      <el-button type=\"primary\" size=\"small\" @click=\"toggleLayer('vec')\">电子地图</el-button>\r\n    </div>\r\n    <div class=\"cesiumContainer\" id=\"cesiumContainer\"></div>\r\n  </div>\r\n</template>\r\n\r\n<script setup>\r\nimport * as Cesium from 'cesium'\r\nimport { onMounted, ref } from 'vue'\r\n\r\nconst selectedType = ref('vec')\r\nlet viewer\r\nlet imageryLayers = []\r\n\r\nconst layerConfig = {\r\n  'img': ['img', 'cia'],\r\n  'vec': ['vec', 'cva']\r\n}\r\nconst toggleLayer = async (type, isInitializing = false) => {\r\n  if (selectedType.value === type && !isInitializing) return\r\n  if (imageryLayers.length) {\r\n    imageryLayers.forEach((v) => viewer.imageryLayers.remove(v))\r\n    imageryLayers = []\r\n  }\r\n  selectedType.value = type\r\n  const layers = layerConfig[type]\r\n  console.log('layers', layers)\r\n  // 天地图影像\r\n  const tk = 'c3d89f4316203ec50d5240dd7b58da1b'\r\n  const layerType = 'w'\r\n  layers.forEach((layer) => {\r\n    console.log('layer', layer)\r\n    const imageryProvider = new Cesium.UrlTemplateImageryProvider({\r\n      url: 'https://t{z}.tianditu.gov.cn/' + layer + '_' + layerType + '/wmts?SERVICE=WMTS&REQUEST=GetTile&VERSION=1.0.0&LAYER=' + layer + '&STYLE=default&TILEMATRIXSET=' + layerType + '&FORMAT=tiles&TILEMATRIX={z}&TILEROW={y}&TILECOL={x}&tk=' + tk,\r\n      subdomains: ['0', '1', '2', '3']\r\n    })\r\n    const currentLayer = viewer.imageryLayers.addImageryProvider(imageryProvider)\r\n    imageryLayers.push(currentLayer)\r\n  })\r\n}\r\n\r\nonMounted(() => {\r\n  viewer = new Cesium.Viewer('cesiumContainer', {\r\n    animation: false, // 是否创建动画小器件，左下角仪表\r\n    baseLayerPicker: false, // 是否显示图层选择器\r\n    fullscreenButton: false, // 是否显示全屏按钮\r\n    vrButton: false, // 用于切换 VR 模式的单个按钮小部件。\r\n    geocoder: false, // //是否显示geocoder小器件，右上角查询按钮\r\n    homeButton: false, // 是否显示Home按钮\r\n    infoBox: false, // 是否显示信息框\r\n    sceneModePicker: false, // 是否显示3D/2D选择器\r\n    selectionIndicator: false, // 是否显示选取指示器组件\r\n    timeline: false, // 是否显示时间轴\r\n    navigationHelpButton: false, // 是否显示右上角的帮助按钮\r\n    navigationInstructionsInitiallyVisible: false,\r\n    scene3DOnly: false, // 如果设置为true，则所有几何图形以3D模式绘制以节约GPU资源\r\n    shouldAnimate: false, // 初始化是否开始动画\r\n    baseLayer: false\r\n  })\r\n  viewer.scene.globe.maximumScreenSpaceError = 1.4 // 减少屏幕空间误差，提高渲染质量\r\n  // 切换图层\r\n  toggleLayer(selectedType.value, true)\r\n\r\n  // 定位中国范围\r\n  const rectangle = Cesium.Rectangle.fromDegrees(73.66, 3.86, 135.05, 53.55)\r\n  viewer.camera.flyTo({\r\n    destination: rectangle,\r\n    orientation: {\r\n      heading: Cesium.Math.toRadians(0),\r\n      pitch: Cesium.Math.toRadians(-90),\r\n      roll: Cesium.Math.toRadians(0)\r\n    },\r\n    duration: 0\r\n  })\r\n})\r\n<\/script>\r\n<style scoped>\r\n.container {\r\n  position: relative;\r\n  width: 100%;\r\n  height: 100%;\r\n  .cesiumContainer {\r\n    width: 100%;\r\n    height: 100%;\r\n    overflow: hidden\r\n  }\r\n  .btn_wrapper {\r\n    position: absolute;\r\n    left: 50px;\r\n    top: 10px;\r\n    z-index: 1;\r\n  }\r\n}\r\n</style>\r\n";export{e as default};
